import { React, useState } from "react";
// import Service from "./Service";

function SearchBar() {
  const [filter, setFilter] = useState("");
  const tasksData = [
    { title: "raz", category: "home", color: "red" },
    { title: "dwa", category: "blah", color: "blue" },
    { title: "razdwa", category: "school", color: "green" },
    { title: "trzy", category: "home", color: "red" },
    { title: "trzynaście", category: "home", color: "red" },
    { title: "raz", category: "workshop", color: "black" },
  ];
  return (
    (<Header filter={filter} setFilter={setFilter} />),
    (<TileContainer tasksData={tasksData} filter={filter} />)
  );
}

const TileContainer = ({ tasksData, filter }) => {
  return (
    <div>
      {tasksData
        ?.filter(({ title }) => {
          const cleaned = filter.toLoweCase().trim();
          return title.toLoweCase().trim().include(cleaned);
        })
        .map(({ title }) => (
          <li>{title}</li>
        ))}
    </div>
  );
};

const Header = ({ setFilter }) => {
  return <SearchInput setFilter={setFilter} />;
};

const SearchInput = ({ setFilter }) => {
  return (
    <input
      id="dzialaj"
      class="nav-input"
      type="text"
      placeholder="Znajdź usługę..."
      onChange={(e) => setFilter(e.target.value)}
    />
  );
};

export default SearchBar;
