import { React, useState } from "react";
import ServicesList from "./ServicesList";

function SearchBar() {
  const [filter, setFilter] = useState("");

  return (
    (<Header filter={filter} setFilter={setFilter} />),
    (<TileContainer ServicesList={ServicesList} filter={filter} />)
  );
}

const TileContainer = ({ ServicesList, filter }) => {
  return (
    <div>
      {ServicesList?.filter(({ title }) => {
        const cleaned = filter.toLowerCase().trim();
        return title.toLowerCase().trim().include(cleaned);
      }).map(({ title }) => (
        <li>{title}</li>
      ))}
    </div>
  );
};

const Header = ({ setFilter }) => {
  return <SearchInput setFilter={setFilter} />;
};

const SearchInput = ({ setFilter }) => {
  return (
    <input
      class="nav-input"
      type="text"
      placeholder="Znajdź usługę..."
      onChange={(e) => setFilter(e.target.value)}
    />
  );
};

export default SearchBar;
