import { React, useState, useEffect } from "react";
import ServicesList from "./ServicesList";
import Service from "./Service.js";

function SearchBar({ services }) {
  // const [services1, setServices] = useState([]);
  // const [filter, setFilter] = useState("");

  const Db = fetch("./data/services.json")
    .then((res) => res.json())
    .then((data) => console.log(data));

  return <FilterableList tasks={Db} />;
}

function FilterableList({ tasks }) {
  const [state, setState] = useState("");
  console.log(tasks);
  return (
    <div>
      <input
        onChange={(e) => setState(e.target.value)}
        placeholder="Znajdź usługę..."
        type="text"
        value="{state}"
      />
      <ul>
        {tasks
          .filter((task) => task.category.includes(state))
          .map((task) => (
            <li>{`${task.category} ${task.city} ${task.price}`}</li>
          ))}
      </ul>
    </div>
  );
}

