import { React, useState, useEffect } from "react";
import ServicesList from "./ServicesList";
import Service from "./Service.js";

function SearchBar({ services }) {
  // const [services, setServices] = useState([]);
  const [search, setSearch] = useState("");
  const [filteredServices, setFilteredServices] = useState([]);
  // const [loading, setLoading] = useState(false);

  // useEffect(() => {
  //   setLoading(true);
  //   fetch("./data/services.json").then((res) => {
  //     setServices(res.services).then(data => {
  //     setService(data.services);;
  //     setLoading(false);
  //   });
  //   .catch((err) => {
  //     console.log(err);
  //   });
  // }, []);

  useEffect(() => {
    setFilteredServices(
      services.filter((service) =>
        service.category.trim().toLowerCase().includes(search.toLowerCase())
      )
    );
  }, [search, services]);

  // if (loading) {
  //   return <p>Szukamy Twojej usługi...</p>;
  // }

  return (
    <div>
      <input
        type="text"
        class="nav-input"
        placeholder="Znajdź usługę..."
        onChange={(e) => setSearch(e.target.value)}
      />
      {filteredServices.map((service, index) => (
        <ServiceDetail key={index} {...service} />
      ))}
    </div>
  );
}

const ServiceDetail = (props) => {
  const { category } = props;

  return (
    <>
      <p>{category}</p>
    </>
  );
};

export default SearchBar;
